
public class Fortest2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 for(int i=1;i<=3;i++)
	 // 1         2  i값이 한번 실행했을 때 얼마인지 생각해보기.
 {
	 for(int j = 1;j<=2;j++)
	 {//       3      4     번호 순서대로 작업한다.
		 System.out.print(i+","+j+"....");
	 }
	 System.out.println();
 }
	}

}
